# Write your query or mutation here
query {
  session(browser: false) {
    user {
      id,
      email
      {
        value
      }
      realFirstName
      {
        value
      }
      createdAt
      displayLastName
      {
         value
      }
      
      
      
    },
    token
  }
}

mutation login($input: LoginInput!) {
  login(input: $input) {
    user {
      id
      fullName
      firstName
      avatarLetters
      createdAt
      email {
        canRead
        canEdit
        value
      }
      realFirstName {
        canRead
        canEdit
        value
      }
      realLastName {
        canRead
        canEdit
        value
      }
      displayFirstName {
        canRead
        canEdit
        value
      }
      displayLastName {
        canRead
        canEdit
        value
      }
      phone {
        canRead
        canEdit
        value
      }
      about {
        canRead
        canEdit
        value
      }
      status {
        canRead
        canEdit
        value
      }
      roles {
        canRead
        canEdit
        value
      }
      title {
        canRead
        canEdit
        value
      }
      timezone {
        canRead
        canEdit
        value {
          name
          lat
          long
          countries {
            code
            name
            zones {
              name
              lat
              long
              countries {
                code
              }
            }
          }
        }
      }
      unavailabilities {
        canRead
        items {
          id
          createdAt
          description {
            canRead
            canEdit
            value
          }
          start
          end
        }
        total
        hasMore
        canCreate
      }
      organizations {
        canRead
        items {
          id
          createdAt
          name {
            canRead
            canEdit
            value
          }
          avatarLetters
        }
        total
        hasMore
        canCreate
      }
      education {
        canRead
        items {
          id
          createdAt
          degree {
            canRead
            canEdit
            value
          }
          major {
            canRead
            canEdit
            value
          }
          institution {
            canRead
            canEdit
            value
          }
        }
        total
        hasMore
        canCreate
      }
    }
  }
}

